mapper.update:
update users
set 
	user_phone = #{user_phone},
	user_email = #{user_email},
	user_pos = #{user_pos}
where username = #{username}

mapper.delete:
delete from users
where username in

    #{username}

mapper.update:
update code
set  group_name=#{group_name}
where group_code=#{group_code};

mapper.delete:
delete from code
where group_code in 

    #{group_code}

mapper.update:
update item_code
set  s_cate_item_name=#{s_cate_item_name}
where s_cate_item_code=#{s_cate_item_code};

mapper.delete:
DELETE FROM item_code
WHERE s_cate_item_code IN 

    #{s_cate_item_code}
